mapper.select:
select * from msg_table
where msg_get_id = #{msg_get_id}
order by msg_no desc

select * from msg_table
where msg_send_id = #{msg_send_id}
order by msg_no desc

	select count(msg_no) 
	from msg_table
    where msg_get_id = #{msg_get_id}

	select count(msg_no) 
	from msg_table
    where msg_send_id = #{msg_send_id}


 	select *
 	from (select rownum rn, msg_no, msg_title, msg_content, msg_get_id, msg_send_id 
        	from msg_table
        	where msg_get_id = #{msg_get_id}
       		and rownum <= #{pageNum} * #{amount}
            order by msg_no desc)
	where rn > (#{pageNum} -1) * #{amount}
